mapper.select:
select id,content,update_time,serial_number,status from task


        and  content like concat ('%',#{content},'%')


        and account = #{account}

mapper.update:
update task
content=#{content}, update_time=#{updateTime},
where id=#{id}

mapper.select:
select * from task


        and content like concat('%',#{content},'%')


        and serial_number=#{serialNumber}


        and status=#{status}

        and account=#{account}
